<span class="line">    <span class="token keyword">if</span> <span class="token punctuation">[</span> <span class="token string">&quot;<span class="token variable">$input_pass</span>&quot;</span> <span class="token operator">=</span> <span class="token string">&quot;<span class="token variable">$correct_pass</span>&quot;</span> <span class="token punctuation">]</span><span class="token punctuation">;</span> <span class="token keyword">then</span></span>
